    std::cout << "*" << move_left << "   ()()   " << move_right << "*\n";
    std::cout << "*" << move_left << "   |..|   " << move_right << "*\n";
    std::cout << "*" << move_left << "   |  |   " << move_right << "*\n";
    std::cout << "*" << move_left << "   |UU|   " << move_right << "*\n";
    std::cout << "*" << move_left << "  --\\/--  " << move_right << "*\n";
    std::cout << "*" << move_left << " /  ||  \\ " << move_right << "*\n";
